// Inputs
const gitlabItems = $('Normailze Data').all().map(i => i.json);
const postgresRows = $('List Postgres Documents').all().map(i => i.json);

// Hilfsfunktion: Zeitstempel in Millisekunden parsen
function parseTimeMs(s) {
  if (!s) return null;
  const t = Date.parse(String(s));
  return Number.isFinite(t) ? t : null;
}

// Build lookup: Postgres by ID (eindeutig!)
const pgById = new Map();
for (const row of postgresRows) {
  if (!row || !row.id) continue;
  pgById.set(row.id, {
    id: row.id,
    lastModifiedMs: parseTimeMs(row.last_modified),
    raw: row,
  });
}

// Compare GitLab items against Postgres
const updatedOrNew = [];

for (const file of gitlabItems) {
  if (!file || !file.id) continue;

  // Lookup direkt √ºber die eindeutige ID
  const pgRec = pgById.get(file.id);

  if (!pgRec) {
    // Neu: ID existiert nicht in Postgres
    updatedOrNew.push({
      json: {
        ...file,
        _changeReason: 'new',
      },
    });
    continue;
  }

  // ID existiert ‚Üí pr√ºfe ob ge√§ndert (via lastModified)
  const glLMms = parseTimeMs(file.lastModified);
  const pgLMms = pgRec.lastModifiedMs;

  let sameLM = false;
  if (pgLMms != null && glLMms != null) {
    // Toleranz von 1 Sekunde f√ºr Rundungsfehler
    const diff = Math.abs(pgLMms - glLMms);
    sameLM = diff <= 1000;
  }

  if (!sameLM) {
    // Ge√§ndert: lastModified unterscheidet sich
    updatedOrNew.push({
      json: {
        ...file,
        _changeReason: 'modified',
        _debug: {
          glLMms,
          pgLMms,
          diffMs: glLMms && pgLMms ? Math.abs(glLMms - pgLMms) : null,
        },
      },
    });
  }
  // Sonst: unver√§ndert ‚Üí nicht in Output
}

return updatedOrNew;

